export const generateFileListFromFileArray = array => {
  // filter only the relevant values
  let newArray = [];

  array.map(a => {
    if (a.isValid && !newArray.includes(a.imageName)) {
      newArray.push(a.imageName);
    }
  });

  return newArray;
};

export const generateBlobUriObjectFromAFileArray = (
  array,
  arrayName,
  blobUrl
) => {
  // filter only the relevant values
  let newArray = generateFileListFromFileArray(array);

  let object = blobUrl ? JSON.parse(blobUrl) : {};
  object[arrayName] = newArray;

  // return it as string
  return JSON.stringify(object);
};

export /**
 * Returns an object, which contains the section, document and fileArray (name and value) from inside a file metadata list.
 *
 * @param {*} fileMetadataList
 * @param {*} filterProperty
 * @param {*} filterValue
 * @returns
 */
const getFileArraysFromFileMetadataList = (
  fileMetadataList,
  filterProperty,
  filterValue
) => {
  let allFileArrays = {};
  let fileMetaDatas = fileMetadataList;
  if (filterProperty && filterValue) {
    fileMetaDatas = fileMetadataList.filter(
      f => f[filterProperty] === filterValue
    );
  }

  if (!arrayNullOrEmpty(fileMetaDatas)) {
    fileMetaDatas.map(f => {
      if (f.blobUrl) {
        const fileArrayObject = JSON.parse(f.blobUrl);
        const arrayName = Object.keys(fileArrayObject)[0];

        let files = fileArrayObject[arrayName].map(f => {
          return {
            isDirty: false,
            isLoading: false,
            isUploaded: true,
            isUploading: false,
            isValid: true,
            imageName: f,
            timeStamp: new Date().toUTCString(),
          };
        });

        allFileArrays[arrayName] = files;
      }
    });
  }

  return allFileArrays;
};

export /**
 * Fills a file array url value with a placeholder base64 image.
 *
 * @param {*} fileArray
 * @returns
 */
const setPlaceHolderOnFileArray = fileArray => {
  let updatedFileArray = cloneDeep(fileArray);

  updatedFileArray.map(f => {
    f.url = scanIcon;
    f.isDirty = false;
    f.isLoading = false;
    f.isUploaded = false;
    f.isUploading = true;
    f.isValid = true;
  });
  return updatedFileArray;
};

export const getFileArrayFromFileNameCSV = async (
  placeholder,
  fileNameCSV,
  appId,
  detailsId,
  section,
  document
) => {
  let fileArray = [];
  if (fileNameCSV) {
    const fileNameList = JSON.parse(fileNameCSV);
    if (!arrayNullOrEmpty(fileNameList)) {
      let image = {};
      image.fileBytes =
        'data:image/svg+xml;base64,';
      const promises = fileNameList.map(async fileName => {
        if (!placeholder) {
          const imgId = `${appId}${
            detailsId ? '.' + detailsId : ''
          }.${section}.${document}.${replaceDotBySymbol(fileName, '_')}`;
          image = await getImage(imgId);
        }
        fileArray.push({
          imageName: fileName,
          isDirty: false,
          isLoading: false,
          isUploaded: true,
          isUploading: false,
          isValid: true,
          url: image ? image.fileBytes : '',
          doc: document,
          placeholder,
          timeStamp: new Date().toUTCString(),
        });
      });

      await Promise.all(promises);
    }
  }
  return fileArray;
};

export const getFileArrayFromBlobUrl = async (
  blobUrl,
  appId,
  detailsId,
  section,
  document
) => {
  let fileArray = [];
  if (!arrayNullOrEmpty(blobUrl)) {
    const promises = blobUrl.map(async fileName => {
      const imgId = `${appId}${
        detailsId ? '.' + detailsId : ''
      }.${section}.${document}.${replaceDotBySymbol(fileName, '_')}`;
      const image = await getImage(imgId);
      //const newUrl = image ? URL.createObjectURL(image) : null;
      fileArray.push({
        imageName: fileName,
        isDirty: false,
        isLoading: false,
        isUploaded: true,
        isUploading: false,
        isValid: true,
        url: image ? image.fileBytes : '',
        timeStamp: new Date().toUTCString(),
      });
    });

    await Promise.all(promises);
  }
  return fileArray;
};
